create database match_summary;
use match_summary;
CREATE TABLE mytable(
   team1     VARCHAR(12) NOT NULL 
  ,team2     VARCHAR(12) NOT NULL
  ,winner    VARCHAR(12) NOT NULL
  ,margin    VARCHAR(10) NOT NULL
  ,matchDate VARCHAR(15) NOT NULL
  ,match_id  VARCHAR(7) NOT NULL
);
select * from mytable;
-- 4 list all matches where the wiining team won by a margin 
