create database players;
use players;
CREATE TABLE mytable(
   name         VARCHAR(21) NOT NULL
  ,team         VARCHAR(12) NOT NULL
  ,battingStyle VARCHAR(14) NOT NULL
  ,bowlingStyle VARCHAR(46) NOT NULL
  ,playingRole  VARCHAR(19) NOT NULL
);
select*from mytable;
-- 6 count the number of players in each playing role(batsman,bowler,all-rounder,etc) in the tournament
select playingRole,count(*) as number_of_players
from mytable group by playingRole
order by number_of_players desc;
